import React, { useState, useEffect } from "react";
import "./App.css";
import Login from "./Login";
import {
  assertTodoExists,
  assertTodoCompleted,
  assertTodoDeleted,
  assertTodoUpdated,
} from "./utils/assertions";

function App() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [editIndex, setEditIndex] = useState(null);
  const [token, setToken] = useState("");
  const [error, setError] = useState("");
  const [assertion, setAssertion] = useState(null);

  // Fetch todos from API
  const fetchTodos = async () => {
    try {
      const response = await fetch("http://localhost:3001/items", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setTodos(data);
      // Clear any previous assertions
      setAssertion(null);
    } catch (err) {
      setError("Failed to fetch todos");
    }
  };

  // Handle login
  const handleLogin = (newToken) => {
    setToken(newToken);
    fetchTodos();
  };

  // Handle logout
  const handleLogout = () => {
    setToken("");
    setTodos([]);
    setInputValue("");
    setEditIndex(null);
  };

  // Handle add/edit todo with assertion
  const handleAddTodo = async () => {
    if (!inputValue.trim()) return;

    try {
      if (editIndex !== null) {
        // Update existing todo
        await fetch(`http://localhost:3001/items/${todos[editIndex].id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ title: inputValue, completed: false }),
        });
        setAssertion(
          assertTodoUpdated(todos, todos[editIndex].title, inputValue)
        );
      } else {
        // Create new todo with default title
        const defaultTitle = "New Todo";
        await fetch("http://localhost:3001/items", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ title: defaultTitle, completed: false }),
        });
        setAssertion(assertTodoExists(todos, defaultTitle));
      }

      setInputValue("");
      setEditIndex(null);
      await fetchTodos();
    } catch (err) {
      setError("Failed to save todo");
    }
  };

  // Handle delete todo with assertion
  const handleDeleteTodo = async (todoId) => {
    try {
      await fetch(`http://localhost:3001/items/${todoId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      await fetchTodos();
      setAssertion(
        assertTodoDeleted(todos, todos.find((t) => t.id === todoId).title)
      );
    } catch (err) {
      setError("Failed to delete todo");
    }
  };

  // Handle toggle todo with assertion
  const handleToggleTodo = async (todoId) => {
    try {
      const todo = todos.find((t) => t.id === todoId);
      await fetch(`http://localhost:3001/items/${todoId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ title: todo.title, completed: !todo.completed }),
      });
      await fetchTodos();
      setAssertion(
        todo.completed
          ? assertTodoCompleted(todos, todo.title)
          : assertTodoExists(todos, todo.title)
      );
    } catch (err) {
      setError("Failed to update todo status");
    }
  };

  // Handle edit todo
  const handleEditTodo = (todo) => {
    setInputValue(todo.title);
    setEditIndex(todos.findIndex((t) => t.id === todo.id));
  };

  // Add assertion display component
  const AssertionMessage = () => {
    if (!assertion) return null;
    return (
      <div
        className={`assertion-message ${
          assertion ? "assertion-error" : "assertion-success"
        }`}
      >
        {assertion}
      </div>
    );
  };

  // Fetch todos when token changes
  useEffect(() => {
    if (token) {
      fetchTodos();
    }
  }, [token]);

  // Show login if not authenticated
  if (!token) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="app">
      <div className="header">
        <h1>Todo App</h1>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}
      <AssertionMessage />

      <div className="todo-container">
        <div className="todo-input-container">
          <input
            type="text"
            placeholder={
              editIndex !== null
                ? "Edit task..."
                : "Add a new task... (will use default title)"
            }
            className="todo-input"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
          />
          <button className="todo-button" onClick={handleAddTodo}>
            {editIndex !== null ? "Save" : "Add"}
          </button>
        </div>
        <div className="todo-list">
          {todos.map((todo) => (
            <div key={todo.id} className="todo-item">
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => handleToggleTodo(todo.id)}
              />
              <span
                className={`todo-text ${todo.completed ? "completed" : ""}`}
              >
                {todo.title}
              </span>
              <div className="todo-actions">
                <button
                  className="todo-edit"
                  onClick={() => handleEditTodo(todo)}
                >
                  ✎
                </button>
                <button
                  className="todo-delete"
                  onClick={() => handleDeleteTodo(todo.id)}
                >
                  ✕
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
